const { oneLine } = require('common-tags');
const Command = require('../base');

module.exports = class DisableCommandCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'disable',
			aliases: ['disable-command', 'cmd-off', 'command-off'],
			group: 'commands',
			memberName: 'disable',
			description: 'Deaktiviert einen Befehl oder eine Gruppe.',
			details: oneLine`
				Das Argument muss der Name / die ID (teilweise oder ganz) eines Befehls oder einer Befehlsgruppe sein.
				Nur Administratoren dürfen diesen Befehl verwenden.
			`,
			examples: ['disable util', 'disable Utility', 'disable prefix'],
			guarded: true,

			args: [
				{
					key: 'cmdOrGrp',
					label: 'command/group',
					prompt: 'Welchen Befehl oder welche Gruppe möchtest du deaktivieren?',
					type: 'group|command'
				}
			]
		});
	}

	hasPermission(msg) {
		if(!msg.guild) return this.client.isOwner(msg.author);
		return msg.member.hasPermission('ADMINISTRATOR') || this.client.isOwner(msg.author);
	}

	run(msg, args) {
		if(!args.cmdOrGrp.isEnabledIn(msg.guild, true)) {
			return msg.reply(
				`\`${args.cmdOrGrp.name}\` ${args.cmdOrGrp.group ? 'Dieser Befehl' : 'Diese Gruppe'} wurde bereits deaktiviert.`
			);
		}
		if(args.cmdOrGrp.guarded) {
			return msg.reply(
				`Du kannst \`${args.cmdOrGrp.name}\` ${args.cmdOrGrp.group ? 'diesen Befehl' : 'diese Gruppe'} nicht deaktivieren.`
			);
		}
		args.cmdOrGrp.setEnabledIn(msg.guild, false);
		return msg.reply(`\`${args.cmdOrGrp.name}\` ${args.cmdOrGrp.group ? 'Befehl' : 'Gruppe'} wurde deaktiviert.`);
	}
};
