const { stripIndents, oneLine } = require('common-tags');
const Command = require('../base');
const { disambiguation } = require('../../util');

module.exports = class HelpCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'help',
			group: 'util',
			memberName: 'help',
			aliases: ['commands'],
			description: 'Schickt dir eine Liste aller verfügbaren Befehle per DM.',
			details: oneLine`
				Der Befehl kann Teil eines Befehlsnamens oder ein ganzer Befehlsname sein.
				Wenn er nicht angegeben ist, werden alle verfügbaren Befehle aufgelistet.
			`,
			examples: ['help', 'help prefix'],
			guarded: true,

			args: [
				{
					key: 'command',
					prompt: 'Für welchen Befehl würdest du gerne die Hilfe anzeigen?',
					type: 'string',
					default: ''
				}
			]
		});
	}

	async run(msg, args) { // eslint-disable-line complexity
		const groups = this.client.registry.groups;
		const commands = this.client.registry.findCommands(args.command, false, msg);
		const showAll = args.command && args.command.toLowerCase() === 'all';
		if(args.command && !showAll) {
			if(commands.length === 1) {
				let help = stripIndents`
					${oneLine`
						Command **${commands[0].name}**: ${commands[0].description}
						${commands[0].guildOnly ? ' (Nur für Server verfügbar)' : ''}
						${commands[0].nsfw ? ' (NSFW)' : ''}
					`}

					**Verwendung:** ${msg.anyUsage(`${commands[0].name}${commands[0].format ? ` ${commands[0].format}` : ''}`)}
				`;
				if(commands[0].aliases.length > 0) help += `\n**Aliases:** ${commands[0].aliases.join(', ')}`;
				help += `\n${oneLine`
					**Gruppe:** ${commands[0].group.name}
					(\`${commands[0].groupID}:${commands[0].memberName}\`)
				`}`;
				if(commands[0].details) help += `\n**Details:** ${commands[0].details}`;
				if(commands[0].examples) help += `\n**Beispiele:**\n${commands[0].examples.join('\n')}`;

				const messages = [];
				try {
					messages.push(await msg.direct(help));
					if(msg.channel.type !== 'dm') messages.push(await msg.reply('Ich habe dir eine DM mit allen Befehlen geschickt.'));
				} catch(err) {
					messages.push(await msg.reply('Ich kann dir die Befehle leider nicht schicken. Vielleicht hast du deine DMs deaktiviert.'));
				}
				return messages;
			} else if(commands.length > 15) {
				return msg.reply('Mehrere Befehle wurden gefunden. Bitte sei etwas genauer.');
			} else if(commands.length > 1) {
				return msg.reply(disambiguation(commands, 'commands'));
			} else {
				return msg.reply(
					`Ich konnte den Befehl nicht finden. Verwende ${msg.usage(
						null, msg.channel.type === 'dm' ? null : undefined, msg.channel.type === 'dm' ? null : undefined
					)}, um alle Commands anzuzeigen.`
				);
			}
		} else {
			const messages = [];
			try {
				messages.push(await msg.direct(stripIndents`
					${oneLine`
						Um einen Befehl bei ${msg.guild ? msg.guild.name : 'einem beliebigen Server'} auszuführen,
						verwende ${Command.usage('command', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
						Beispiel: ${Command.usage('prefix', msg.guild ? msg.guild.commandPrefix : null, this.client.user)}.
					`}
					Um einen Befehl in dieser DM auszuführen, verwende einfach ${Command.usage('command', null, null)} ohne Prefix.

					Verwende ${this.usage('<command>', null, null)} um alle Informationen zum Befehl zu erhalten.
					Verwende ${this.usage('all', null, null)} um *alle* Befehle anzuzeigen. Nicht nur die Verfügbaren.

					**${showAll ? 'Alle Befehle' : `Verfügbare Befehle bei ${msg.guild || 'dieser DM'}`}**

					${(showAll ? groups : groups.filter(grp => grp.commands.some(cmd => cmd.isUsable(msg))))
						.map(grp => stripIndents`
							**${grp.name}**:
							${(showAll ? grp.commands : grp.commands.filter(cmd => cmd.isUsable(msg)))
								.map(cmd => `**${cmd.name}:** ${cmd.description}${cmd.nsfw ? ' (NSFW)' : ''}`).join('\n')
							}
						`).join('\n\n')
					}
				`, { split: true }));
				if(msg.channel.type !== 'dm') messages.push(await msg.reply('Ich habe dir eine DM mit allen Befehlen geschickt.'));
			} catch(err) {
				messages.push(await msg.reply('Ich kann dir die Befehle leider nicht schicken. Vielleicht hast du deine DMs deaktiviert.'));
			}
			return messages;
		}
	}
};
