const { oneLine } = require('common-tags');
const Command = require('../base');

module.exports = class PingCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'ping',
			group: 'util',
			memberName: 'ping',
			description: 'Überprüft den Ping des Bot\'s zum Server.',
			throttling: {
				usages: 5,
				duration: 10
			}
		});
	}

	async run(msg) {
		if(!msg.editable) {
			const pingMsg = await msg.reply('Pinging...');
			return pingMsg.edit(oneLine`
				${msg.channel.type !== 'dm' ? `${msg.author},` : ''}
				Pong! Die Nachrichtenrundreise dauerte ${pingMsg.createdTimestamp - msg.createdTimestamp}ms.
				${this.client.ping ? `Der Heartbeat-Ping beträgt ${Math.round(this.client.ping)}ms.` : ''}
			`);
		} else {
			await msg.edit('Pinging...');
			return msg.edit(oneLine`
				Pong! Die Nachrichtenrundreise dauerte ${msg.editedTimestamp - msg.createdTimestamp}ms.
				${this.client.ping ? `Der Heartbeat-Ping beträgt ${Math.round(this.client.ping)}ms.` : ''}
			`);
		}
	}
};
