const { stripIndents, oneLine } = require('common-tags');
const Command = require('../base');

module.exports = class PrefixCommand extends Command {
	constructor(client) {
		super(client, {
			name: 'prefix',
			group: 'util',
			memberName: 'prefix',
			description: 'Zeigt den aktuellen Prefix an oder ändert ihn',
			format: '[prefix/"default"/"none"]',
			details: oneLine`
				Wenn kein Prefix angegeben ist, wird der aktuelle Prefix angezeigt.
				Bei "default" wird der Prefix zum Standard zurückgesetzt.
				Bei "none" wird der Prefix entfernt und nur noch der Mention-Prefix ist vorhanden.
				Nur Administratoren können den Prefix ändern.
			`,
			examples: ['prefix', 'prefix -', 'prefix omg!', 'prefix default', 'prefix none'],

			args: [
				{
					key: 'prefix',
					prompt: 'Zu was soll der Prefix geändert werden?',
					type: 'string',
					max: 15,
					default: ''
				}
			]
		});
	}

	async run(msg, args) {
		// Just output the prefix
		if(!args.prefix) {
			const prefix = msg.guild ? msg.guild.commandPrefix : this.client.commandPrefix;
			return msg.reply(stripIndents`
				${prefix ? `Der aktuelle Prefix ist \`${prefix}\`.` : 'Es gibt aktuell keinen Prefix.'}
				Um Commands auszuführen, verwende ${msg.anyUsage('command')}.
			`);
		}

		// Check the user's permission before changing anything
		if(msg.guild) {
			if(!msg.member.hasPermission('ADMINISTRATOR') && !this.client.isOwner(msg.author)) {
				return msg.reply('Nur Administratoren können den Prefix ändern.');
			}
		} else if(!this.client.isOwner(msg.author)) {
			return msg.reply('Nur der / die Bot-Besitzer dürfen diesen Befehl verwenden.');
		}

		// Save the prefix
		const lowercase = args.prefix.toLowerCase();
		const prefix = lowercase === 'none' ? '' : args.prefix;
		let response;
		if(lowercase === 'default') {
			if(msg.guild) msg.guild.commandPrefix = null; else this.client.commandPrefix = null;
			const current = this.client.commandPrefix ? `\`${this.client.commandPrefix}\`` : 'kein Prefix';
			response = `Prefix wurde zum Standard zurückgesetzt (Aktuell: ${current}).`;
		} else {
			if(msg.guild) msg.guild.commandPrefix = prefix; else this.client.commandPrefix = prefix;
			response = prefix ? `Prefix wurde geändert zu \`${args.prefix}\`.` : 'Prefix wurde entfernt.';
		}

		await msg.reply(`${response} Um Commands auszuführen, verwende ${msg.anyUsage('command')}.`);
		return null;
	}
};
